CREATE DATABASE DB_FACILITY;
USE DB_FACILITY;

-- SEOUL_FACILITY DATABASE

drop table tbl_facility;

CREATE TABLE TBL_FACILITY(
	GUBUN VARCHAR(20) ,
	SVCID VARCHAR(20),
	MAXCLASSNM VARCHAR(20) ,	
	MINCLASSNM VARCHAR(20) ,
	SVCSTATNM VARCHAR(20) ,
	SVCNM VARCHAR(100) ,
	PAYATNM VARCHAR(20) ,
	PLACENM VARCHAR(50) ,
	USETGTINFO VARCHAR(40) ,
	SVCURL VARCHAR(200) ,
	X VARCHAR(20) ,
	Y VARCHAR(20) ,
	SVCOPNBGNDT DATETIME ,
	SVCOPNENDDT DATETIME ,
	RCPTBGNDT DATETIME ,
	RCPTENDDT DATETIME ,
	AREANM VARCHAR(20) ,
	IMGURL VARCHAR(200) ,
	DTLCONT LONGTEXT,
	TELNO VARCHAR(50) ,
	V_MIN VARCHAR(5) ,
	V_MAX VARCHAR(5) ,
	REVSTDDAYNM VARCHAR(20) ,
	REVSTDDAY VARCHAR(5),
	PRIMARY KEY(SVCID)
);

-- 연습용 sql문
SELECT COUNT(*) FROM TBL_FACILITY
WHERE (
    MINCLASSNM LIKE '%축구%'
    OR PLACENM LIKE '%축구%'
    OR AREANM LIKE '%축구%'
);

SELECT DISTINCT SVCNM 
FROM TBL_FACILITY 
WHERE SVCSTATNM = '안내중' 
   OR SVCSTATNM = '접수중';
   
SELECT * FROM TBL_FACILITY WHERE SVCNM = '보라매공원테니스장 3번코트(평일야간)' AND (SVCSTATNM = '안내중' OR SVCSTATNM = '접수중');
SELECT SVCID FROM TBL_FACILITY WHERE SVCNM = '보라매공원테니스장 3번코트(평일주간)' AND (SVCSTATNM = '안내중' OR SVCSTATNM = '접수중');
SELECT SVCID FROM TBL_FACILITY WHERE SVCSTATNM = '안내중';
