CREATE TABLE TBL_ADMIN_MEMBER(
	A_M_NO 			INT AUTO_INCREMENT,
	A_M_APPROVAL	INT	DEFAULT 0,
	A_M_ID			VARCHAR(20),
	A_M_PW			VARCHAR(100),
	A_M_NAME		VARCHAR(20),
	A_M_GENDER		CHAR(1),
	A_M_MAIL		VARCHAR(50),
	A_M_PHONE		VARCHAR(20),
	A_M_REG_DATE 	DATETIME	DEFAULT NOW(),
	A_M_MOD_DATE	DATETIME	DEFAULT NOW(),
    A_M_ROLE		VARCHAR(10) DEFAULT 'ADMIN',
	PRIMARY KEY(A_M_NO)
);

CREATE TABLE TBL_USER_MEMBER(
	U_M_NO 			INT AUTO_INCREMENT,
	U_M_ID			VARCHAR(20),
	U_M_PW			VARCHAR(100),
	U_M_NAME		VARCHAR(20),
	U_M_GENDER		CHAR(1),
	U_M_MAIL		VARCHAR(50),
	U_M_PHONE		VARCHAR(20),
	U_M_REG_DATE 	DATETIME	DEFAULT NOW(),
	U_M_MOD_DATE	DATETIME	DEFAULT NOW(),
    U_M_ROLE		VARCHAR(10) DEFAULT 'USER',
	PRIMARY KEY(U_M_NO)
);

select * from TBL_USER_MEMBER;	
drop table TBL_USER_MEMBER;

CREATE TABLE TBL_RESERVE_INFO (
	R_NO		INT AUTO_INCREMENT,
	SVCID		VARCHAR(20),
	U_M_ID		VARCHAR(20),
	R_RESERVE_DATE	VARCHAR(50),
	R_REG_DATE	DATETIME DEFAULT NOW(),
	R_MOD_DATE	DATETIME DEFAULT NOW(),
	R_USE_STATE	VARCHAR(10),
	R_USE_TIME	VARCHAR(50),
	PRIMARY KEY (R_NO)
);
drop table TBL_RESERVE_INFO;

CREATE TABLE TBL_COMMENT (
	C_NO		INT AUTO_INCREMENT,
	SVCID		VARCHAR(20),
	U_M_ID		VARCHAR(20),
	C_TEXT		VARCHAR(1000),
	C_RANK		INT,
	C_REG_DATE	DATETIME,
	C_MOD_DATE	DATETIME,
	PRIMARY KEY (C_NO)
);




